//import { getWords } from "./dataModules";
import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [words, setWords] = useState("");
  const [answer, setAnswer] = useState(null);
  //const testWords = getWords();
  console.log(answer, "the chooooooooosen");
  useEffect(() => {
    function getWords() {
      const request = axios.get("http://localhost:3001/data");

      return request.then((response) => {
        //   console.log("this is response from module 1",response.data)

        return setWords(response.data);
      });
    }
    getWords();
  }, []);

  const handleAnswer=(e)=>{
    setAnswer(e.target.value)
  }
  //submit answer
  const submitWord = (e) => {
    e.preventDefault();
    console.log("Form submitted");
  };

  console.log("this is the data", words);
  return (
    <>
      <div className="main-container">
        <section>
          <h1 className="title">
            Choose the right answer that describe this word
          </h1>
          <div className="main-word">
            {" "}
            <h2>{words[0]?.word}</h2>
          </div>
        </section>
        <section>
          <form onSubmit={(e) => submitWord(e)}>
            <div>
              <input
                checked={""}
                onChange={(e) => handleAnswer(e)}
                name="wordType"
                id="noun"
                type="radio"
                value="noun"
              />
              <label htmlFor="noun"> Noun</label>
              <input
                onChange={(e) => setAnswer(e.target.value)}
                name="wordType"
                id="verb"
                type="radio"
                value="verb"
              />
              <label htmlFor="verb"> Verb</label>
              <input
                onChange={(e) => setAnswer(e.target.value)}
                name="wordType"
                id="adjective"
                type="radio"
                value="adjective"
              />
              <label htmlFor="adjective"> Adjective</label>
              <input
                onChange={(e) => setAnswer(e.target.value)}
                name="wordType"
                id="adverb"
                type="radio"
                value="adverb"
              />
              <label htmlFor="adverb"> Adverb</label>
            </div>
            <div>
              <button type="submit" className="btn-confirm">
                {" "}
                Confirm{" "}
              </button>
            </div>
          </form>
        </section>
      </div>
      <footer>
        Progress:
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
      </footer>
    </>
  );
}

export default App;
