const ProgressBar = ({ correctAnswers, numberOfQ }) => {
  return (
    <footer>
      Progress:
      {numberOfQ.map((i, index) => {
      return  <span
          className={
            correctAnswers[index] === true
              ? "right"
              : correctAnswers[index] === false
              ? "wrong"
              : ""
          }></span>;
      })
      }
    
    </footer>
  );
};

export default ProgressBar;
